$('a[href="#"]').click(function (e) {
    e.preventDefault();
});

var id = $("body").data("img-box");

if (id !== undefined ) {
    var box = $("#" + id + " .img-box"),
        k = +box.css("max-width").slice(0, -2) / +box.css("max-height").slice(0, -2)
    ;
    $(document).ready(function () {
        box.height(box.width() / k);
    });
    $(window).resize(function () {
        box.height(box.width() / k);
    });
}